#! python 3""" Creates quizzes with questions and answers in random order, along with the answer key."""import random# The quiz data. Keys are states and values are their capitals.capitals = {'Alabama': 'Montgomery', 'Alaska': 'Juneau', 'Arizona': 'Phoenix',            'Arkansas': 'Little Rock', 'California': 'Sacramento', 'Colorado': 'Denver',            'Connecticut': 'Hartford', 'Delaware': 'Dover', 'Florida': 'Tallahassee',            'Georgia': 'Atlanta', 'Hawaii': 'Honolulu', 'Idaho': 'Boise', 'Illinois':            'Springfield', 'Indiana': 'Indianapolis', 'Iowa': 'Des Moines', 'Kansas':            'Topeka', 'Kentucky': 'Frankfort', 'Louisiana': 'Baton Rouge', 'Maine':            'Augusta', 'Maryland': 'Annapolis', 'Massachusetts': 'Boston', 'Michigan':            'Lansing', 'Minnesota': 'Saint Paul', 'Mississippi': 'Jackson', 'Missouri':            'Jefferson City', 'Montana': 'Helena', 'Nebraska': 'Lincoln', 'Nevada':            'Carson City', 'New Hampshire': 'Concord', 'New Jersey': 'Trenton', 'New Mexico': 'Santa Fe',            'New York': 'Albany', 'North Carolina': 'Raleigh', 'North Dakota': 'Bismarck', 'Ohio': 'Columbus',            'Oklahoma': 'Oklahoma City','Oregon': 'Salem', 'Pennsylvania': 'Harrisburg', 'Rhode Island': 'Providence',            'South Carolina': 'Columbia', 'South Dakota': 'Pierre', 'Tennessee':            'Nashville', 'Texas': 'Austin', 'Utah': 'Salt Lake City', 'Vermont':            'Montpelier', 'Virginia': 'Richmond', 'Washington': 'Olympia', 'West Virginia': 'Charleston',            'Wisconsin': 'Madison', 'Wyoming': 'Cheyenne'}for quizNum in range(35):    # Generate 35 quiz files    # Create quiz files and answers key    quizFile = open('capitalquiz%s.txt' % (quizNum + 1), 'w')    answerKeyFile = open('capitalquiz_answers%s.txt' % (quizNum + 1), 'w')    # The quiz data. Keys are states and values are their capitals.    quizFile.write('Name:\n\nDate:\n\nPeriod:\n\n')    quizFile.write((' ' * 20) + 'State Capitals Quiz (Form %s)' % (quizNum + 1))    quizFile.write('\n\n')# Shuffle the order of the states.states = list(capitals.keys())random.shuffle(states)# Loop through all 50 states, making a question for each.for questionNum in range(50):    # Getting wrong and right answers    # Correct answer in the capitals dictionary. Loop through the states in the shuffled states list.    # Find each state in capitals, and store that states's corresponding capital in correctAnswer.    correctAnswer = capitals[states[questionNum]]    # Possible wrong answers could be gotten it by duplicating all the values in the capitals dictionary    wrongAnswers = list(capitals.values())    # Delete the correct answer    del wrongAnswers[wrongAnswers.index(correctAnswer)]    # Select 3 random values from the list. 1st arg -- the list you wanna select from,    # 2nd -- the number of values you want to select    wrongAnswers = random.sample(wrongAnswers, 3)    # Full list of answer options is the combination of these 3 wrong answers with the correct answers    answersOptions = wrongAnswers + [correctAnswer]    # Answers need to be randomize    random.shuffle(answersOptions)